package com.example.devapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;

import java.util.ArrayList;

public class Activity7 extends AppCompatActivity {
    private ArrayList<String> gambar = new ArrayList<>();
    private ArrayList<String> nama = new ArrayList<>();


    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_7);

        get();
    }

    private void tahapRV(){
        RecyclerView recyclerView = findViewById(R.id.recycleView);
        Recycleview_Adapter adapter = new Recycleview_Adapter(gambar, nama, this);

        recyclerView.setAdapter(adapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this,LinearLayoutManager.VERTICAL,false));

    }

    private void get (){

        nama.add("Inter Milan");
        gambar.add("data:image/jpeg;base64,");

        nama.add("A.C. Milan");
        gambar.add("data:image/png;base64,");

        nama.add("Juventus F.C.");
        gambar.add("data:image/png;base64,");

        nama.add("S.S.C. Napoli");
        gambar.add("data:image/png;base64,");

        nama.add("Atalanta B.C.");
        gambar.add("data:image/png;base64,");

        nama.add("A.S. Roma");
        gambar.add("data:image/png;base64,");

        tahapRV();

    }
}